INSERT INTO `rs_posts` VALUES (766,1,'2019-04-29 16:07:48','2019-04-29 19:07:48','<p>[vc_row full_width=\"stretch_row\" css=\".vc_custom_1556215966382{background-image: url(http://responsiblesoy.loc/wp-content/uploads/2019/04/foto-header-productores.jpg?id=398) !important;background-position: center !important;background-repeat: no-repeat !important;background-size: cover !important;}\" el_class=\"header-seccion py-lg\"][vc_column][vc_row_inner el_class=\"pb-sm\"][vc_column_inner][vc_custom_heading text=\"Productores\" font_container=\"tag:h1|text_align:center|color:%23ffffff\" use_theme_fonts=\"yes\" el_class=\"extra-bold\"][/vc_column_inner][/vc_row_inner][vc_row_inner el_class=\"pb-xs\"][vc_column_inner width=\"1/2\" offset=\"vc_col-lg-offset-3 vc_col-lg-6 vc_col-md-offset-3 vc_col-md-6 vc_col-sm-offset-3 vc_col-xs-12\"][vc_custom_heading text=\"Ser productor RTRS es un compromiso con la responsabilidad social y ambiental. Es regirse por cinco principios básicos: cumplir con las leyes y las buenas prácticas empresariales, proveer buenas condiciones laborales, respetar y generar lazos con las comunidades locales, cuidar el medio ambiente, y llevar adelante prácticas agrícolas adecuadas.\" font_container=\"tag:div|text_align:center|color:%23ffffff\" use_theme_fonts=\"yes\"][/vc_column_inner][/vc_row_inner][vc_row_inner el_class=\"text-center pb-sm\"][vc_column_inner][vc_raw_html]JTNDYSUyMGhyZWYlM0QlMjIlMjMlMjIlMjBjbGFzcyUzRCUyMmJ1dHRvbi1pbmZvLWhvbWUlMjBweS1uYSUyMHB4LXNtJTIyJTNFVm9sdW1lbiUyMGRlJTIwcHJvZHVjY2klQzMlQjNuJTIwZGUlMjBzb2phJTIwUlRSUyUzQyUyRmElM0U=[/vc_raw_html][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row el_class=\"py-sm\"][vc_column][vc_row_inner][vc_column_inner][vc_custom_heading text=\"Conozca a nuestros productores certificados\" use_theme_fonts=\"yes\" el_class=\"extra-bold\"][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row el_class=\"py-md\"][vc_column][vc_row_inner el_class=\"pb-sm\"][vc_column_inner][vc_custom_heading text=\"¿Por qué certificarse RTRS?\" use_theme_fonts=\"yes\" el_class=\"extra-bold\"][/vc_column_inner][/vc_row_inner][vc_row_inner][vc_column_inner el_class=\"pb-sm\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es un sello al compromiso del productor en materia de responsabilidad social\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][vc_column_inner el_class=\"pb-sm\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_single_image image=\"418\" img_size=\"full\"][/vc_column_inner][vc_column_inner el_class=\"pb-xs\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es implementable a nivel mundial: es global y adecuado a la legislación de cada país productor de soja en todo el mundo.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][vc_column_inner el_class=\"pb-xs\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es aplicable a la producción de soja con múltiples destinos: consumo humano, alimentos balanceados para consumo animal, biocombustibles.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][vc_column_inner el_class=\"pb-xs\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es apto para productores de todo tipo y tamaño: certificación individual, de grupo o multisitio.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][/vc_row_inner][vc_row_inner][vc_column_inner el_class=\"pb-sm\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_single_image image=\"417\" img_size=\"full\"][/vc_column_inner][vc_column_inner el_class=\"pb-sm\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es aplicable a todo tipo tecnología de producción.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][vc_column_inner el_class=\"pb-xs\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es una herramienta de gestión y una estrategia sostenible: permite estar preparado para los requerimientos del mercado global.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][vc_column_inner el_class=\"pb-xs\" width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_single_image image=\"416\" img_size=\"full\"][/vc_column_inner][vc_column_inner width=\"1/5\" offset=\"vc_col-lg-1/5 vc_col-md-1/5 vc_col-xs-12\"][vc_custom_heading text=\"Es una solución a las metas globales de sustentabilidad: mercados comprometidos con cadenas de abastecimiento responsables.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\"][/vc_column_inner][/vc_row_inner][vc_row_inner el_class=\"pt-sm\"][vc_column_inner][vc_separator border_width=\"2\"][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row el_class=\"pb-md\"][vc_column width=\"1/3\"][vc_single_image image=\"423\" img_size=\"full\"][/vc_column][vc_column width=\"2/3\"][vc_custom_heading text=\"Ser productor certificado es un sello de:\" font_container=\"tag:h5|text_align:left|color:%2352871c\" use_theme_fonts=\"yes\" el_class=\"font-bold pb-sm\"][vc_row_inner][vc_column_inner width=\"1/3\"][vc_custom_heading text=\"Competitividad\" font_container=\"tag:div|text_align:left|color:%2352871c\" use_theme_fonts=\"yes\" el_class=\"font-medium\"][vc_custom_heading text=\"en el marco de una demanda específica de un mercado global cada vez más exigente con las características del producto que demanda.\" font_container=\"tag:div|text_align:left|color:%2383836c\" use_theme_fonts=\"yes\" el_class=\"font-medium\"][/vc_column_inner][vc_column_inner width=\"1/3\"][vc_custom_headi